function DNA (chromosomes)
{


	playButton = '<div class="row"><div class="col-md-1"></div><div class="col-md-10"><a href="#" class="btn btn-default btn-xs btn-block play">Play</a></div><div class="col-md-1"></div></div>';
	
	DNADiv = $('<div/>', {
        'class':'col-md-3 DNA',
        'html': chromosome(chromosomes) + playButton
    });
  	
  	

    return DNADiv;
}

function chromosome (objects)
{
	var html = '';
	var chromosomeDiv = '';
	$.each( objects, function( i, object ) {
		chromosomeDiv = chromosomeDiv + '<div class="col-md-5 chromosome" style="background-color:' + object.color + '"><input type="checkbox"></div>';
		if(i % 2 != 0)
		{
			chromosomeDivs = '<div class="row"><div class="col-md-1"></div>' + chromosomeDiv + '<div class="col-md-1"></div></div>';
		    console.log(i);
		    console.log(chromosomeDiv);
		    chromosomeDiv = '';
		    html = html + chromosomeDivs;
		    
		}
	});

	return html;

}


$( document ).ready(function() {
	testobjects = {0: {"color": "#CCC"}, 1: {"color": "#CCC"}};
    $('.DNA-row').append(DNA(testobjects));
});

